import React from 'react'
import WeatherTile from './WeatherTile'
import DayNightCycle from './DayNightCycle'
import './WeatherSection.css'
import moment from 'moment'

class WeatherSection extends React.Component {
  constructor(props) {
    super(props)
    this.RequestWeather = this.RequestWeather.bind(this)
    this.Sunset = this.Sunset.bind(this)


    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
    }
  }

  RequestWeather() {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=2`)
    //fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      console.log(json)
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        })
        //console.log(moment().format('LT'))
        console.log(this.state.weatherData[0].astro.sunset)
        this.Sunset()

      }
    ).catch(() => {
      console.log('error')
    })
  }

  componentDidMount() {
    this.RequestWeather()
    setInterval(this.Sunset, 59000)
    setInterval(this.RequestWeather, 60000)
  }

  Sunset() {
    console.log('SUNSET1: ' + this.state.weatherData[0].astro.sunset)
    let sunsetOffset = moment.duration({'minutes': 15}),
    sunsetStarter = moment(this.state.weatherData[0].astro.sunset, 'hh:mmA').subtract(sunsetOffset),
    sunriseStarter = moment(this.state.weatherData[0].astro.sunrise, 'hh:mmA').subtract(15, 'minutes')
    console.log('SUNSET2: ' + sunsetStarter)
    if (moment().format('hh:mm A') === sunsetStarter) {
      let tillSunset = sunsetStarter.subtract(moment())
      const sunsetTimer = setTimeout(() => {
        document.querySelector('.dayNightCircle').style.animation = 'dayNightCycle 10s infinite 1s forward'
      }, tillSunset)
    }
  }

  render() {
    return(
    <div className="weatherBackground">
    <DayNightCycle />
    <div className="Tiles">
    <div className="nowWeather">
    {this.state.isLoading ?
    <WeatherTile icon={this.state.currentData.condition.icon} date="Now" today={this.state.currentData.temp_c} /> : null}
    </div>
    <div className="forecast">
  {this.state.isLoading ?
    this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={moment(data.date, 'YYYY-MM-DD').format('MMM D')} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
    </div>
    </div>
    <div className="weatherHouse"></div>
    </div>
    )
  }
}
export default WeatherSection

