import React from 'react'
import './App.css';
import WeatherTile from './WeatherTile'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null
    }
  }

  componentDidMount() {
    this.setState({isLoading: false})
    fetch('http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=5')
    .then(response => response.json())
    .then(json => {
      this.setState({
        isLoading: true,
        weatherData: json.forecast.forecastday
      })
    })
  }
  render() {
    return (
      <div className="App">
      {this.state.isLoading ?
        this.state.weatherData.map(data => (<WeatherTile main={data.date} />)) : null }
      </div>
    );
  }
}

export default App;
