import React from 'react'
import './App.css';
import WeatherTile from './WeatherTile'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null
    }
  }

  componentDidMount() {
    fetch('http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=5')
    .then(response => response.json())
    .then(json => {
      this.setState({
        weatherData: json.forecast.forecastday
      })
    })
  }
  render() {
    return (
      <div className="App">
      { this.state.weatherData.map(data => {
        console.log(data)
        return <WeatherTile main={data.date} />
      }) }
      </div>
    );
  }
}

export default App;
