  InitSun() {
    //console.log('FIRED')
    //const now = moment(new Date(), 'ddd MMM DD YYYY HH:mm')
    let now = moment().set({'year': 2021, 'month': 3, 'date': 21, 'hour': 20, 'minute': 30})
    now = moment(now, 'ddd MMM DD YYYY HH:mm')
    console.log(now)
    let tomorrow = new Date()
