    const sunTimes = suncalc.getTimes(now, this.props.latitude, this.props.longitude)
    console.log('SUNTIMES')
    console.log(sunTimes)

    const isThisForToday = (pos) => {
        return moment(sunTimes[pos], 'ddd MMM DD YYYY HH:mm')
    }

    const times = [
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
