  CheckSeason() {
    if (this.props.season == 0) {
      return <div className="houseGround" id="winter"><div className="snowman" /></div>
    } else if (this.props.season == 1) {
    return <div className="houseGround" id="spring"><div className="shortGrass"><div className="flowers" /></div></div>
    } else if (this.props.season == 2) {
      return <div className="houseGround" id="summer"><div className="tallGrass"><div className="garden" /></div></div>
    } else if (this.props.season == 3) {
    return <div className="houseGround" id="fall"><div className="leaves" /></div>
    } else {
      return <div classame="houseGround" />
    }
  }

  render() {
    const waxOrWane = (current) => {
      if (this.state.sunPosition === current) {
        return this.state.skyWaxing
      } else {
        if (current === this.state.skies[this.state.sunPosition].prev) {
          return this.state.skyWaning
        }
      }
    }
    return(
      <div className={this.state.isDay == 1 ? "sky skyDay" : "sky skyNight"}>
        <div id="nauticalDusk" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'dusk'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="dusk" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'dusk'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="night" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'night'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="nadir" className={`skyComponentNadir ${this.state.skyOpacity}  ${this.state.skyClass === 'nadir'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="nightEnd" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'nightEnd'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="dawn" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'dawn'? this.state.skyWaxing : this.state.skyWaning}`}><div className="stars" /></div>
        <div id="sunrise" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="sunriseEnd" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunriseEnd'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="sunset" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="noon" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'noon'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="goldenHourEnd" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'goldenHourEnd'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="sunsetStart" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="sunset" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between1" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between2" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between3" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between4" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between5" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between6" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between7" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between8" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between9" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between10" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />
        <div id="between11" className={`skyComponent ${this.state.skyOpacity}  ${this.state.skyClass === 'sunrise'? this.state.skyWaxing : this.state.skyWaning}`} />


        <div id="sky" className={this.state.sunPosition}>
          <div className="day"></div>
          <div className="Sun">
            <div className={`sunColor ${this.state.sunOpacity}`} id="sunColorDuskDawn" />
            <div className={`sunColor ${this.state.sunOpacity}`} id="sunColorSunriseEndSunriseStart" />
            <div className={`sunColor ${this.state.sunOpacity}`} id="sunColorSunriseSunset" />
            <div className={`sunColor ${this.state.sunOpacity}`} id="sunColorGoldens" />
            <div className={`sunColor ${this.state.sunOpacity}`} id="sunNoon" />
          </div>
          <div className="Moon" />
        </div>
        <div className={this.props.isDay == 0 ? "houseContainer houseNight" : "houseContainer"}>
          {this.CheckSeason()}
        <div className="house" />
        </div>
        {this.props.isDay == 0 ? <div className="houseLight">
          <div className="lightYellow hLight" />
          <div className="lightOrange hLight" />
        </div> : null}

      </div>
    )
  }
}
