import React from 'react'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';

import './App.css';
import WeatherSection from './WeatherSection'
import Calendar from './CalendarComponents/Calendar';
import PicsNNews from './PicsNNews/PicsNNews'

const moment = extendMoment(Moment);

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null,
      isDay: 1
    }
  }
  getLocation = (weather) => {
    console.log('APPLEVEL: ' + weather)
    this.setState({location: weather}, this.RequestWeather)
  }

/*  componentDidUpdate() {
    if (this.state.isDay == 0) {
      document.querySelector('.calendarDiv').classList.add('calNight');
      for (var cal in document.querySelectorAll('.calendar')) {
        cal.classList.add('calNight')
      }
    } else if (this.state.isDay == 1) {
      document.querySelector('.calendarDiv').classList.remove('calNight');
      for (var cal in document.querySelectorAll('.calendar')) {
        cal.classList.remove('calNight')
      }
    }
  }*/

  componentDidMount() {
    const isDay = setInterval(() => {
      const sunTimes = suncalc.getTimes(new Date(), 49.895077, -97.138451);
      if (moment().isBefore(moment(sunTimes.dusk, 'ddd MMM DD YYYY HH:mm'))) {
        this.setState({
          isDay: 0
        })
      } else {
        this.setState({
          isDay: 1
        })
      }
    }, 60000)
  }
  render() {
    return (
      <div className="App">
        <Calendar isDay={this.state.isDay}/>
        <PicsNNews isDay={this.state.isDay}/>
        <WeatherSection isDay={this.state.isDay}/>
      </div>
    );
  }
}

export default App;
