import React from 'react'
import './App.css';
import WeatherTile from './WeatherTile'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null
    }
  }

  componentDidMount() {
    fetch('http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=auto:ip&days=5')
    .then(response => response.json())
    .then(json => {
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
      })
    })
  }
  render() {
    return (
      <div className="App">
        <div className="Tiles">
        {this.state.isLoading ?
        <WeatherTile icon={this.state.currentData.condition.icon} date='Now' today={this.state.currentData.temp_c} /> : null}
      {this.state.isLoading ?
        this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={data.date} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
        </div>
      </div>
    );
  }
}

export default App;
