import React from 'react'
import moment from 'moment'
import './Calendar.css'
import CalendarDay from './CalendarDay'

export default class Calendar extends React.Component {
  constructor(props) {
    super(props)
  }


  componentDidMount() {
    if (this.props.isDay == 0) {
      document.querySelectorAll('.calendar').forEach(calendar => {
        calendar.classList.add('calNight');
      })
    } else {
      document.querySelectorAll('.calendar').forEach(calendar => {
        calendar.classList.remove('calNight');
      })
    }
  }
  render() {
    return(
    <div className="calendarDiv">
      <CalendarDay />
      <CalendarDay />
      <CalendarDay />
      <CalendarDay />
      <CalendarDay />
      <CalendarDay />
      <CalendarDay />
    </div>
    )
  }
}
