import React from 'react'
import './DayNightCycle.css'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';
const moment = extendMoment(Moment);


export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: '',
      sunTimes: null
    }
    //this.checkSunState = this.checkSunState.bind(this)
    this.SunPos = this.SunPos.bind(this)
    //this.InitSun = this.InitSun.bind(this)
  }

  SunPos() {
    const sun = document.querySelector('.Sun')
    const moon = document.querySelector('.Moon')
    const sky = document.querySelector('.night')
    const sunYellow = document.querySelector('.sunYellow')
    const sunOrange = document.querySelector('.sunOrange')
    const sunRed = document.querySelector('.sunRed')

    const positions = ['noon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', '' ]
    const now = new Date()
    console.log('TIME: ' + this.state.sunPosition)
    this.setState({
      sunTimes: suncalc.getTimes(now, 49.895077, -97.138451)
    })
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() == sunTimes[i].getHours()) {
        sunTimes[i].setMinutes(sunTimes[i].getMinutes(-15))
        if (now.getMinutes() == sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] == sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                })
              }
            }
          }
        }
      }
    }
    if (this.state.sunPosition !== null) {
      if ( this.state.sunPosition == 'sunrise') {
        sky.style.animation = "sunriseToEnd 5s forwards"
        sky.style.animationDuration = '';
        sky.style.animationFillMode = 'forwards';

        sun.style.animation = "sunDawnToSunrise"
        sun.style.animationDuration = '5s';
        sun.style.animationFillMode = 'forwards'
        sun.style.animationDirection = 'forwards'

        moon.classList.remove('opacity2')
        moon.classList.add('opacity1')

      } else if (this.state.sunPosition == 'sunriseEnd') {
          sky.style.animation = "sunriseEndToGoldenEnd"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunriseToEnd"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'
          sun.style.animationDirection = 'forwards'

      } else if (this.state.sunPosition == 'goldenHourEnd') {
          sky.style.animation = "goldenEndToNoon"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunriseEndToGoldenEnd"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'
          sun.style.animationDirection = 'forwards'

          moon.classList.remove('opacity1')
          moon.classList.add('opacity0')

      } else if (this.state.sunPosition == 'solarNoon') {
          sky.style.animation = "noonToGolden"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunGoldenEndToSolarNoon"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'
          sun.style.animationDirection = 'forwards'

      } else if (this.state.sunPosition == 'goldenHour') {
          sky.style.animation = "goldenToSunsetStart"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunGoldenEndToSolarNoon"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'
          sun.style.animationDirection = 'reverse'

      }  else if (this.state.sunPosition == 'sunsetStart') {
          sky.style.animation = "sunsetStartToSunset"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunGoldenToSunsetStart"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

       } else if (this.state.sunPosition == 'sunset') {
           sky.style.animation = "sunsetToDusk"
           sky.style.animationDuration = '5s';
           sky.style.animationFillMode = 'forwards';

           sun.style.animation = "sunSunsetStartToSunset"
           sun.style.animationDuration = '5s';
           sun.style.animationFillMode = 'forwards'

           moon.classList.remove('opacity0')
           moon.classList.add('opacity1')

      }  else if (this.state.sunPosition == 'dusk' || this.state.sunPosition == 'nauticalDusk') {
          sky.style.animation = "duskToNight"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunsetToDusk"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity1')
          moon.classList.add('opacity2')

      } else if (this.state.sunPosition == 'night') {
          sky.style.animation = "nightToNatis"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunDuskToNight"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity2')
          moon.classList.add('opacity3')

      } else if (this.state.sunPosition == 'natis') {
          sky.style.animation = "natisToNightEnd"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunNightToNatis"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity3')
          moon.classList.add('opacity4')

      } else if (this.state.sunPosition == 'nightEnd') {
          sky.style.animation = "nightEndToDawn"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunNadirToNightEnd"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity4')
          moon.classList.add('opacity3')

      } else if (this.state.sunPosition == 'dawn' || this.state.sunPosition == 'nauticalDawn') {
          sky.style.animation = "dawnToSunrise"
          sky.style.animationDuration = '5s';
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunNightEndToDawn"
          sun.style.animationDuration = '5s';
          sun.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity3')
          moon.classList.add('opacity2')
      }
    }
  }

