           skyColor.style.animation = "skySunsetToDusk"
           skyColor.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
