          sun.style.animationFillMode = 'forwards'

          skyColor.style.animation = "skyDawnToSunrise"
          skyColor.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          skyColor.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity3')
          moon.classList.add('opacity2')
      }
      sky.addEventListener("animationend", () => {
        sky.style.animation = '';
      })
    }
  }

