  InitSun() {
    const now = moment(new Date(), 'ddd MMM DD YYYY HH:mm')
    const sunTimes = suncalc.getTimes(now, 49.895077, -97.138451)
    const times = [
      moment(sunTimes.nadir, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nightEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nauticalDawn, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.dawn, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunrise, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunriseEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.goldenHourEnd, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.solarNoon, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.goldenHour, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunsetStart, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.sunset, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.dusk, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.nauticalDusk, 'ddd MMM DD YYYY HH:mm'),
      moment(sunTimes.night, 'ddd MMM DD YYYY HH:mm')
    ]
    let tomorrow = new Date()
    tomorrow = tomorrow.setDate(tomorrow.getDate()+1)
    const ranges = [
      moment.range(times[0], times[1]),
      moment.range(times[1], times[2]),
      moment.range(times[2], times[3]),
      moment.range(times[3], times[4]),
      moment.range(times[4], times[5]),
      moment.range(times[5], times[6]),
      moment.range(times[6], times[7]),
      moment.range(times[7], times[8]),
      moment.range(times[8], times[9]),
      moment.range(times[9], times[10]),
      moment.range(times[10], times[11]),
      moment.range(times[11], times[12]),
      moment.range(times[12], times[13]),
      moment.range(times[13], moment(suncalc.getTimes(tomorrow, 49.895077, -97.138451)).nadir)
    ];

    for (let i = 0; i < ranges.length; i++) {
      if (now.within(ranges[i])) {
        if (i == 0) {
          this.setState({
            sunPosition: 'nadir'
          })
        }
          else if (i == 1) {
            this.setState({
              sunPosition: 'nightEnd'
            })
          }
        else if (i == 2) {
          this.setState({
            sunPosition: 'nauticalDawn'
          })
        }
      else if (i == 3) {
        console.log('SSSDFDFDFDF')
        this.setState({
          sunPosition: 'dawn'
        })
      }
        else if (i == 4) {
          this.setState({
            sunPosition: 'sunrise'
          })
        }
        else if (i == 5) {
          this.setState({
            sunPosition: 'sunriseEnd'
          })
        }
        else if (i == 6) {
          this.setState({
            sunPosition: 'goldenHourEnd'
          })
        }
          else if (i == 7) {
            console.log('DUUUUSK')
            this.setState({
              sunPosition: 'solarNoon'
            })
          }
        else if (i == 8) {
          this.setState({
            sunPosition: 'goldenHour'
          })
        }
      else if (i == 9) {
        this.setState({
          sunPosition: 'sunsetStart'
        })
      }
        else if (i == 10) {
          this.setState({
            sunPosition: 'sunset'
          })
        }
        else if (i == 11) {
          this.setState({
            sunPosition: 'nauticalDusk'
          })
        }
        else if (i == 12) {
          this.setState({
            sunPosition: 'dusk'
          })
        }
        else if (i == 13) {
          this.setState({
            sunPosition: 'night'
          })
        }
      }
    }
  }

