import React from 'react'
import moment from 'moment'
import './Calendar.css'
import CalendarDay from './CalendarDay'

export default class Calendar extends React.Component {
  constructor(props) {
    super(props)
    this.GetEvents = this.GetEvents.bind(this)
    this.state = {
      events: {}
    }
  }


  GetEvents() {
    let thisWeek = {
      0: [],
      1: [],
      2: [],
      3: [],
      4: [],
      5: [],
      6: []
    }
    let today = new Date()
    let oneWeek = new Date()
    oneWeek.setDate(oneWeek.getDate() + 6)
    fetch(`https://www.googleapis.com/calendar/v3/calendars/thdnkns@gmail.com/events?singleEvents=True&orderBy=startTime&timeMin=${today.toISOString()}&timeMax=${oneWeek.toISOString()}&key=AIzaSyDZgdchyvtfLwtT2s1JH5dxgnwd-2nI2dw`)
      .then(res => res.json())
      .then(events => {
        let eventsList = [...events.items]
        console.log(Date.parse(eventsList[0].start.dateTime))
        eventsList.forEach(event => {
          if (new Date(Date.parse(event.start.date)).getDate() == today.getDate()) {
            thisWeek[0].push(event);
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 1) {
            thisWeek[1].push(event)
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 2) {
            thisWeek[2].push(event)
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 3) {
            thisWeek[3].push(event)
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 4) {
            thisWeek[4].push(event)
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 5) {
            thisWeek[5].push(event)
          } else if (new Date(Date.parse(event.start.date)).getDate() == today.getDate() + 6) {
            thisWeek[6].push(event)
          }
        })
        this.setState({
          events: thisWeek
        })
      })
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    this.GetEvents()
  }

  render() {
    return(
    <div className="calendarDiv">
      <CalendarDay events={this.state.events[0]} date={moment().format('MMM D')}/>
      <CalendarDay events={this.state.events[1]} date={moment().add(1, 'days').format('MMM D')}/>
      <CalendarDay events={this.state.events[2]} date={moment().add(2, 'days').format('MMM D')}/>
      <CalendarDay events={this.state.events[3]} date={moment().add(3, 'days').format('MMM D')}/>
      <CalendarDay events={this.state.events[4]} date={moment().add(4, 'days').format('MMM D')}/>
      <CalendarDay events={this.state.events[5]} date={moment().add(5, 'days').format('MMM D')}/>
      <CalendarDay events={this.state.events[6]} date={moment().add(6, 'days').format('MMM D')}/>
    </div>
    )
  }
}
