import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.ShuffleNews = this.ShuffleNews.bind(this)
    this.state = {
      newsJson: null,
      headlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard.json')
    .then(res => res.json())
    .then(newsLinks => {
      /*let headlines = [];
        for (let x = 0; x <= 2; x++) {
          headlines.push(newsLinks.data.children[Math.floor(Math.random() * newsLinks.data.children.length)])
        }
        this.setState({
          headlines: headlines
        })*/
        this.setState({
          headlines: newsLinks.data.children
        })
    })
  }

  componentDidUpdate() {
    //this.GetNews()
    this.ShuffleNews()
  }

  componentDidMount() {
    this.GetNews()
    const getNews = setInterval(this.GetNews, 600000);
  }
  async ShuffleNews() {
    await this.GetNews();
    console.log(this.state.headlines)
    let posts = [...this.state.headlines];
    for (var x = this.state.headlines.length; x > 0; x--) {
      setTimeout(() => {
        posts.push(this.state.headlines[x]);
        posts.shift();

      }, 10000)
    }
    this.setState({
      headlines: posts
    })
  }
  render() {
    return(
      <div className="news">
      {this.state.headlines != null ? this.state.headlines.slice(0,3).map(headline => {
        return <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
  }
}
