import React from 'react'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';

import './App.css';
import WeatherSection from './WeatherSection'
import Calendar from './CalendarComponents/Calendar';
import PicsNNews from './PicsNNews/PicsNNews'

const moment = extendMoment(Moment);

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
      //isDay: 1
    }
    this.isDay = this.isDay.bind(this)
  }

  getLocation = (weather) => {
    console.log('APPLEVEL: ' + weather)
    this.setState({location: weather}, this.RequestWeather)
  }

  isDay() {
    /*this.setState({
      isDay: 0
    })*/
    const sunTimes = suncalc.getTimes(new Date(), 49.895077, -97.138451);
    if (moment().within(moment.range(moment(sunTimes.dawn, 'ddd MMM DD YYYY HH:mm'), moment(sunTimes.dusk)))) {
    console.log('IS WITHIN RANGE')
    if (this.state.isDay != 1) {
      this.setState({
        isDay: 1
      })
    }
  } else {
    console.log('IS NOT WITHIN RANGE')
    if (this.state.isDay != 0) {
      this.setState({
        isDay: 0
      })
    }
  }
}

  componentDidMount() {
    /*this.setState({
      isDay: 1
    })*/
    this.isDay()
    let isDayInterval = setInterval(this.isDay, 60000)
  }

  render() {
    return (
      <div className={this.state.isDay == 0 ? 'App opacity3' : 'App'}>
        <Calendar isDay={this.state.isDay}/>
        <WeatherSection isDay={this.state.isDay}/>
        <PicsNNews isDay={this.state.isDay}/>
      </div>
    );
  }
}

export default App;
