import React from 'react'
import './Lightning.css'

export default class Lightning extends React.Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    var rand = Math.round(Math.random() * (30000 - 500)) + 500; // generate new time (between 3sec and 500"s)
    const lightningAnims = ['lightningStrike1 0.25s', 'lightningStrike2 0.5s', 'lightningStrike3 0.75s']
    const lightningAnim = setInterval(() => {
      console.log('TRIGGERED')
      document.querySelector('.lightning').style.animation = 'lightningStrike 0.25s';

    }, rand)
  }
  render() {
    return(
      <div className="lightning" />
    )
  }
}
