import React from 'react'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';

import './App.css';
import WeatherSection from './WeatherSection'
import Calendar from './CalendarComponents/Calendar';
import PicsNNews from './PicsNNews/PicsNNews'

const moment = extendMoment(Moment);

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
      //isDay: 1
    }
    this.isDay = this.isDay.bind(this)
  }

  getLocation = (weather) => {
    console.log('APPLEVEL: ' + weather)
    this.setState({location: weather}, this.RequestWeather)
  }

  isDay() {
    const sunTimes = suncalc.getTimes(new Date(), 49.895077, -97.138451);
    if (moment().within(moment.range(moment(sunTimes.sunrise, 'ddd MMM DD YYYY HH:mm'), moment(sunTimes.sunset)))) {
    //if (moment().isBefore(moment(sunTimes.dusk, 'ddd MMM DD YYYY HH:mm'))) {
    console.log('IS WITHIN RANGE')
      this.setState({
        isDay: 1
      })
    } else {
      console.log('IS NOT WITHIN RANGE')
      this.setState({
        isDay: 0
      })
    }
  }
  componentDidUpdate(prevState) {
  }

  componentDidMount() {
    /*this.setState({
      isDay: 1
    })*/

    this.isDay()
  }

  render() {
    return (
      <div className={"App" + this.state.isDay == 0 ? 'opacity1' : null}>
        <Calendar isDay={this.state.isDay}/>
        <WeatherSection isDay={this.state.isDay}/>
        <PicsNNews isDay={this.state.isDay}/>
      </div>
    );
  }
}

export default App;
