import React from 'react'
import './HeavySnow.css'

export default class HeavySnow extends React.Component {
  constructor(props) {
    super(props)
    this.random = this.random.bind(this)
  }
  random(min, max) {
    return Math.random() * (max - min + 1) ;
  }
  componentDidMount() {
    const snowCanvas = document.getElementById('snowflakesHeavy');
    const ctx = snowCanvas.getContext('2d');
    const snowflakes = 250;
    const snowArray = [];
    let w, h;
    w = snowCanvas.width = window.innerWidth;
    h = snowCanvas.height = window.innerHeight;

    function createSnowflakes() {
      for(let i = 1; i < snowflakes; i++) {
        snowArray.push({
          x: Math.random() * w,
          y: Math.random() * h,
          opacity: Math.random(),
          speedX: this.random(-11, 11),
          speedY: this.random(7, 15),
          radius: this.random(0.5, 4.2)
        })
      }
    }

    function drawSnowflakes() {
      for(let i = 0; i < snowflakes; i++) {
        const gradient = ctx.createRadialGradient(
          snowArray[i].x,
          snowArray[i].y,
          0,
          snowArray[i].x,
          snowArray[i].y,
          snowArray[i].radius
        );
        gradient.addColorStop(0, `rgba(255,255,255,${snowArray[i].opacity})`);
        gradient.addColorStop(0, 'rgba(0,0,0,0)');
        ctx.beginPath();
        ctx.arc(
          snowArray[i].x,
          snowArray[i].y,
          snowArray[i].radius,
          0,
          Math.PI*2,
          false
        );
        ctx.fillStyle = gradient;
        ctx.fill();
      }
    }

    function moveSnowflakes() {
      for (let i = 0; i < snowArray.length; i++) {
        snowArray[i].x = snowArray[i].speedX;
        snowArray[i].y = snowArray[i].speedY

        if (snowArray[i].y > h) {
          snowArray[i].x = Math.random() * w * 1.5;
          snowArray[i].y = -50;
        }
      }
    }

    function updateSnowfall() {
      ctx.clearRect(0 ,0, w, h);
      drawSnowflakes();
      moveSnowflakes();
    }

    setInterval(updateSnowfall, 50);
    createSnowflakes();
  }
  render() {
    return(
    <div className="heavySnowContainer">
      <canvas id="snowflakesHeavy"></canvas>
    </div>
    )
  }

}
