import React from 'react'
import './PartlyCloudy.css'
export default class PartlyCloudy extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isDay: this.props.isday
    }
  }
  componentDidMount() {
  }

  render() {
    this.props.isDay == 0 ? document.querySelectorAll('.cloud').forEach(cloud => {
      cloud.classList.add('cloudNight')
    }) : document.querySelectorAll('.cloud').forEach(cloud => {
      cloud.classList.remove('cloudNight')
    })
   return(
    <div className="cloudsContainerPartly">
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
      <div className="cloudPartly cloud"></div>
    </div>
  )
}
}
