import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.state = {
      newsJson: null,
      headlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard.json')
    .then(res => res.json())
    .then(newsLinks => this.setState({
      newsJson: newsLinks.children
    }))
    const headlines = [];
      for (let x = 0; x < 2; x++) {
        headlines.append(Math.floor(Math.random() * this.state.newsJson.length))
      }
      this.setState({
        headlines: headlines
      })
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    //const getNews = setInterval(this.GetNews, 600000);
  }

  render() {
    return(
      <div className="news">
      {this.state.headlines.map(headline => {
        return(
          <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
        )
      })}
      </div>
    )
  }
}
