export const initialState = {
   isDay: true,
};

const reducer = (state, action) => {
    switch(action.type) {
        case 'SET_DAYTIME':
            return {
                ...state,
                isDay: action.isDay
            };

        default:
            return state;
    }
}

export default reducer
