import React from 'react'
import WeatherTile from './WeatherTile'
import DayNightCycle from './DayNightCycle'
//Cloud modes
import CloudsCloudy from './WeatherComponents/Clouds/CloudsCloudy'
import PartlyCloudy from './WeatherComponents/Clouds/PartlyCloudy'
import Overcast from './WeatherComponents/Clouds/Overcast'
import WindyClouds from './WeatherComponents/Clouds/WindyClouds'
//Snow modes
import LightSnow from './WeatherComponents/Snow/LightSnow'
import MedSnow from './WeatherComponents/Snow/MedSnow'
import HeavySnow from './WeatherComponents/Snow/HeavySnow'
import Blizzard from './WeatherComponents/Snow/Blizzard'
import BlowingSnow from './WeatherComponents/Snow/BlowingSnow'

//Rain modes
import LightRain from './WeatherComponents/Rain/LightRain'
import MedRain from './WeatherComponents/Rain/MedRain'
import HeavyRain from './WeatherComponents/Rain/HeavyRain'
import TorrRain from './WeatherComponents/Rain/TorrRain'

//Storm modes
import Lightning from './WeatherComponents/Thunderstorm/Lightning'

import Fog from './WeatherComponents/Mist&Fog/Fog'
import Mist from './WeatherComponents/Mist&Fog/Mist'

import './WeatherSection.css'
import moment from 'moment'
import Clock from './Clock/Clock';

//import { Calendar, momentLocalizer } from 'react-big-calendar';
// Setup the localizer by providing the moment (or globalize) Object
// to the correct localizer.
//const localizer = Calendar.momentLocalizer(moment) // or globalizeLocalizer

export default class WeatherSection extends React.Component {
  constructor(props) {
    super(props)
    this.RequestWeather = this.RequestWeather.bind(this)

    this.state = {
      isLoading: false,
      weatherData: null,
      location: null,
      skyPos: {}
    }
  }

  RequestWeather() {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=2`)
    //fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        isDay: json.current.is_day,
        code: json.current.code
        })
        this.checkSunState();
        this.CheckSunPos();

      }
    ).catch(() => {
      console.log('error')
    })
  }
  componentDidUpdate() {
    if (this.state.isDay == 0) {
      document.querySelector('.clock').classList.add('clockNight');
      document.querySelectorAll('cloud').forEach(cloud => {
        cloud.classList.add('cloudNight')
      })
    }
  }
  componentDidMount() {
    this.RequestWeather();

    if (this.state.isDay == 0) {
      document.querySelector('.clock').classList.add('clockNight');
      document.querySelectorAll('cloud').forEach(cloud => {
        cloud.classList.add('cloudNight')
      })}


    setInterval(this.checkSunState, (60000 * 5))
    setInterval(this.RequestWeather, (60000 * 5))
  }

  render() {
    return(
    <div className="weatherBackground">
      <div className="Tiles">
        <div className="house"></div>
        <DayNightCycle isday={this.state.isDay}/>
<BlowingSnow />
<PartlyCloudy isDay={this.state.isDay}/>
        <Clock isday={this.state.isDay}/>
        <div className="nowWeather">
        {this.state.isLoading ?
        <WeatherTile isday={0} className="tileCold" condition={this.state.currentData.condition.text} icon={this.state.currentData.condition.icon} date="Now" today={Math.round(this.state.currentData.temp_c)} /> : null}
        </div>
          <div className="forecast">
        {this.state.isLoading ?
          this.state.weatherData.map(data => (<WeatherTile isday={this.state.isDay} className="tileCold" condition={this.state.currentData.condition.text} icon={data.day.condition.icon} date={moment(data.date, 'YYYY-MM-DD').calendar(null, {
           lastDay : '[Yesterday]',
           sameDay : '[Today]',
           nextDay : '[Tomorrow]'
         })} high={Math.round(data.day.maxtemp_c)} low={Math.round(data.day.mintemp_c)}/>)) : null }
        </div>
      </div>
    </div>
    )
  }
}

