import React from 'react'
import picOne from './1.jpg'
import picTwo from './2.jpg'
import picThree from './3.jpg'
import picFour from './4.jpg'
import vidOne from './1.mp4'
import vidTwo from './2.mp4'
import vidThree from './3.mp4'

import ('./PicsNNews.css')

export default class PicsNNews extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      pic: picT
    }
  }
  componentDidMount() {
    const pics = [
      picOne,
      picTwo,
      picThree,
      picFour,
      vidOne,
      vidTwo,
      vidThree
    ]
    const picChooser = setInterval(() => {
      this.setState({
        pic: pics[Math.floor(Math.random() * pics.length)]
      })
    }, 10000)
  }
  render() {
    return(
      <div className="picsNNews">
        <div className="Pics"></div>
        <div className="News">
        {this.state.pic.split('.')[1] == 'mp4'? <video src={this.state.pic} /> : <img src={this.state.pic} />}
        </div>
      </div>
    )
  }
}
