import React from 'react'
import './DayNightCycle.css'
import dayBackground from './Backgrounds/day1.jpg'
import nightBackground from './Backgrounds/night1.jpg'
import sunsetBackground from './Backgrounds/sunset.png'
import sunriseBackground from './Backgrounds/sunrise.jpg'
import duskBackground from './Backgrounds/dusk.jpg'
import dawnBackground from './Backgrounds/dawn.jpg'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';
const moment = extendMoment(Moment);



export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: null,
      sunTimes: null,
      isDay: props.isDay
    }
    this.SunPos = this.SunPos.bind(this)
    this.setSunAnims = this.setSunAnims.bind(this)
    this.InitSun = this.InitSun.bind(this)
    //this.CheckIsDay = this.CheckIsDay.bind(this)

  }

  SunPos() {
    const now = new Date()
    console.log('TIME: ' + this.state.sunPosition)
    this.setState({
      sunTimes: suncalc.getTimes(now, 49.895077, -97.138451)
    })
    this.setState({
      sunPosition: 'goldenHourEnd'
    })
    /*const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() === sunTimes[i].getHours()) {
        sunTimes[i].setMinutes(sunTimes[i].getMinutes(-15))
        if (now.getMinutes() === sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] === sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                  //sunPosition: 'sunset'
                })
              }
            }
          }
        }
      }
    }*/

  }

  setSunAnims() {
    function getNextSunPos(pos) {
      const positions = ['solarNoon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', 'night', 'nadir', 'nightEnd', 'nauticalDawn', 'dawn', 'sunrise', 'sunriseEnd', 'goldenHourEnd']
      let nextPos = positions.indexOf(pos) +1;
      if (nextPos >= positions.length) {
        nextPos = 0;
        return(positions[nextPos])
      } else {
        return(positions[nextPos])
      }
    }

    const sun = document.querySelector('.Sun')
    const moon = document.querySelector('.Moon')
    const sky = document.querySelector('.night')
    const skyColor = document.querySelector('.sky')
    const dusk = document.getElementById('dusk')
    const night = document.getElementById('night')
    const natis = document.getElementById('natis')
    const nightEnd = document.getElementById('nightEnd')
    const dawn = document.getElementById('dawn')
    const sunrise = document.getElementById('sunrise')
    const sunriseEnd = document.getElementById('sunriseEnd')
    const goldenHourEnd = document.getElementById('goldenHourEnd')
    const noon = document.getElementById('noon')

    if (this.state.sunPosition !== null) {
      if ( this.state.sunPosition === 'sunrise') {
        if (this.state.animPos != 0) {
          document.body.style.backgroundImage = `url(${sunriseBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
          sky.style.animation = "sunriseToEnd 5s forwards"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunriseToEnd"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'

          sunrise.style.opacity = '1';
          sunrise.style.animation = "opacityReverse"
          sunrise.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunrise.style.animationFillMode = 'forwards'

          sunriseEnd.style.animation = "opacityForward"
          sunriseEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunriseEnd.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity2')
          moon.classList.add('opacity1')
          this.setState({
            animPos: 0
          })
        }
      } else if (this.state.sunPosition === 'sunriseEnd') {
        if (this.state.animPos != 1) {
          document.body.style.backgroundImage = `url(${dayBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
        console.log('MOMENTCHRCK2: ' + moment())
        console.log('MOMENTCHRCK3: ' + (Math.abs(moment(this.state.sunTimes[this.state.sunPosition], 'ddd MMM DD YYYY HH:mm:ss') - moment()) / 1000))

          sky.style.animation = "sunriseEndToGoldenEnd"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunriseEndToGoldenEnd"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'

          sunriseEnd.style.opacity = '1';
          sunriseEnd.style.animation = "opacityReverse"
          sunriseEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunriseEnd.style.animationFillMode = 'forwards'

          goldenHourEnd.style.animation = "opacityForward"
          goldenHourEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          goldenHourEnd.style.animationFillMode = 'forwards'
          moon.classList.add('opacity0')
          this.setState({
            animPos: 1
          })
        }

      } else if (this.state.sunPosition === 'goldenHourEnd') {
        if (this.state.animPos != 2) {
          document.body.style.backgroundImage = `url(${dayBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
          sky.style.animation = "goldenEndToNoon"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunGoldenEndToSolarNoon"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'

          goldenHourEnd.style.opacity = '1';
          goldenHourEnd.style.animation = "opacityReverse"
          goldenHourEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          goldenHourEnd.style.animationFillMode = 'forwards'

          noon.style.animation = "opacityForward"
          noon.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          noon.style.animationFillMode = 'forwards'

          moon.classList.add('opacity0')
          this.setState({
            animPos: 2
          })
        }

      } else if (this.state.sunPosition === 'solarNoon') {
        if (this.state.animPos != 3) {
          document.body.style.backgroundImage = `url(${dayBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
          sky.style.animation = "noonToGolden"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';
          /*skyColor.style.animation = 'skyNoonToGolden';
          skyColor.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          skyColor.style.animationFillMode = 'forwards';*/
          noon.style.opacity = '1';
          noon.style.animation = "opacityReverse"
          noon.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          noon.style.animationFillMode = 'forwards'

          goldenHourEnd.style.animation = "opacityForward"
          goldenHourEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          goldenHourEnd.style.animationFillMode = 'forwards'

          sun.style.animation = "sunNoonToGolden"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'
          moon.classList.add('opacity0')
          this.setState({
            animPos: 3
          })
        }
      } else if (this.state.sunPosition === 'goldenHour') {
        if (this.state.animPos != 4) {
          document.body.style.backgroundImage = `url(${dayBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
          sky.style.animation = "goldenToSunsetStart"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          goldenHourEnd.style.opacity = '1';
          goldenHourEnd.style.animation = "opacityReverse"
          goldenHourEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          goldenHourEnd.style.animationFillMode = 'forwards'

          sunriseEnd.style.animation = "opacityForward"
          sunriseEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunriseEnd.style.animationFillMode = 'forwards'

          sun.style.animation = "sunGoldenToSunsetStart"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'
          moon.classList.add('opacity0')
          this.setState({
            animPos: 4
          })
        }
      }  else if (this.state.sunPosition === 'sunsetStart') {
        if (this.state.animPos != 5) {
          document.body.style.backgroundImage = `url(${dayBackground})`;
          document.querySelectorAll('.cloud').forEach(cloud => {
            cloud.classList.remove('cloudNight')
          })
          sky.style.animation = "sunsetStartToSunset"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          /*sun.style.animation = "sunSunsetStartToSunset"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'*/

          sunriseEnd.style.opacity = '1';
          sunriseEnd.style.animation = "opacityReverse"
          sunriseEnd.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunriseEnd.style.animationFillMode = 'forwards'

          sunrise.style.animation = "opacityForward"
          sunrise.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sunrise.style.animationFillMode = 'forwards'

          moon.classList.remove('opacity0')
          moon.classList.add('opacity1')
          this.setState({
            animPos: 5
          })
        }
       } else if (this.state.sunPosition === 'sunset') {
         if (this.state.animPos != 6) {
           document.body.style.backgroundImage = `url(${sunsetBackground})`;
           document.querySelectorAll('.cloud').forEach(cloud => {
             cloud.classList.remove('cloudNight')
           })
           sky.style.animation = "sunsetToDusk"
           sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
           sky.style.animationFillMode = 'forwards';

           sun.style.animation = "sunSunsetToDusk"
           sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
