      nauticalDawn: 'rotate(185deg)',
      dawn: 'rotate(185deg)',
      sunrise: 'rotate(230deg)',
      sunriseEnd:'rotate(240deg)',
      goldenHourEnd: 'rotate(270deg)',
      solarNoon: 'rotate(0deg)',
      goldenHour: 'rotate(90deg)',
      sunsetStart: 'rotate(110deg)',
      sunset: 'rotate(150deg)',
      dusk: 'rotate(175deg)',
      nauticalDusk: 'rotate(175deg)',
      night:'rotate(175deg)'
    }
    document.querySelector('.night').style.transform = sunPositions[`${this.state.sunPosition}`]
    this.InitSun()
    console.log(suncalc.getTimes(new Date(), 49.895077, -97.138451))
    this.SunPos()
    setTimeout(() => {
      this.SunPos()
    }, 1000)
    const sunPosTimer = setInterval(this.InitSun, 60000 * 5)
    this.CheckIsDay()
