          })
        }
        else if (i == 22) {
          this.setState({
            sunPosition: 'between9'
          })
        }
        else if (i == 23) {
          this.setState({
            sunPosition: 'between10'
          })
        }
        else if (i == 24) {
          this.setState({
            sunPosition: 'between11'
          })
        }
      }
    }
  }

