import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    //this.ShuffleNews = this.ShuffleNews.bind(this)

    this.state = {
      newsJson: null,
      headlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children];
      this.setState({
        headlines: posts
      })

      console.log('posts : ' + posts)
      let posts1 = document.querySelectorAll('.newsHeadline')
      for (var x = newsLinks.data.children.length; x > 0; x--) {
        setTimeout(() => {
          posts.pop()
          posts1[0].remove()
          this.setState({
            headlines: posts
          })
        }, 5000)
      }
    })
  }

  componentDidUpdate() {
    //this.GetNews()
    //this.ShuffleNews()
  }

  componentDidMount() {
    this.GetNews()
    const getNews = setInterval(this.GetNews, 600000);
  }

  render() {
    return(
      <div className="news">
      {this.state.headlines != null ? this.state.headlines.slice(0,3).map(headline => {
        return <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
  }
}
