import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.state = {
      newsJson: null,
      headlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard.json')
    .then(res => res.json())
    .then(newsLinks => {
      const headlines = [];
        for (let x = 0; x < 2; x++) {
          headlines.append(newsLinks.children[Math.floor(Math.random() * newsLinks.children.length)])
        }
        this.setState({
          headlines: headlines
        })
    })
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    this.GetNews()
    //const getNews = setInterval(this.GetNews, 600000);
  }

  render() {
    return(
      <div className="news">

      </div>
    )
  }
}
