import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.state = {
      newsJson: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard.json')
    .then(res => res.json())
    .then(newsLinks => this.setState({
      newsJson: newsLinks
    }))
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    this.GetNews()
    //const getNews = setInterval(this.GetNews, 600000);
  }

  render() {
    return(
      <Headline />
    )
  }
}
