import React from 'react'
import './WeatherTile.css';

export default class WeatherTile extends React.Component {
  constructor(props) {
    super(props)
    this.NightTileChecker = this.NightTileChecker.bind(this)
    this.state = {
      isLoading: false,
      isDay: this.props.isDay
    }
  }
  NightTileChecker() {
    if (this.state.isDay == 0) {
      document.querySelectorAll('.tile').forEach(x => {
        x.classList.add('tileNight')
      })
    } else if (this.state.isDay == 1) {
      document.querySelectorAll('.tile').forEach(x => {
        x.classList.remove('tileNight')
      })
    }
  }

  componentDidUpdate(prevProps) {
    if (prevProps.isDay !== this.props.isDay) {
      this.NightTileChecker()
    }
  }
  componentDidMount(props) {
    //this.NightTileChecker()
  }
  render(props) {

    let weather;
    this.props.today ? weather = <p>{this.props.today}</p> : weather = <p>{this.props.high} / {this.props.low}</p>

    return(
      <div className={this.props.isDay == 0 ? 'tile tileNight' : 'tile'}>
          <p className="tileDate">{this.props.date}</p>
          {weather}
          <p id="tileCondition">{this.props.condition}</p>
          {this.props.icicles? <div className='icicles'></div> : null}
      </div>
    )
  }
}
