import React, {useState, useEffect} from 'react'
import Headline from './Headline'

import ('./News.css')

export default function News() {
  const [newsJson, getNewsJson] = useState(null);
  const [headlines, getHeadlines] = useState(false);
  const [shownHeadlines, getShownHeadlines] = useState([])
  const [visible, setVisible] = useState(false)
  const [animConsts, setAnimConsts] = useState({indexes: [0, 1, 2], timeout: 250})
  const [newsInit, isNewsInit] = useState(false);

  useEffect(() => {
    GetNews()
  }, [])

  useEffect(() => {
    if (shownHeadlines !== null) {
      console.log('shownHeadline')
      if (shownHeadlines.length >= 3) {
        animConsts.indexes.forEach(index => {
          setTimeout(() => {
            document?.getElementById(`headline${index}`)?.classList.remove('opacity0')
          }, animConsts.timeout*(index+1))
        })
      } else {
        GetNews();
      }
    }
  }, [shownHeadlines]);

  useEffect(() => {
    if (headlines !== null) {
      console.log('headline')
      setInterval(async() => {
        animConsts.indexes.forEach(index => {
          /*setTimeout(() => {
            document?.getElementById(`headline${index}`)?.classList?.add('opacity0')
          }, animConsts.timeout*(index+1))*/
          setTimeout(() => {
            document?.getElementById(`headline${index}`)?.classList?.add('opacity0')
            if(index == 2) {
               getShownHeadlines([...shownHeadlines.slice(3,)])
            }
          }, 1000)
        })

      }, 1000 * 10)
    }
  }, [headlines])

  const GetNews = () => {
    fetch('https://old.reddit.com/user/news-bot-dash/m/news/top.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children];
      getHeadlines(true)
       getShownHeadlines(posts);
    })
  }

    return(
      <div className="news">
      {shownHeadlines ? shownHeadlines.slice(0,3).map((headline, index) => {
        return <Headline id={`headline${index}`} title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : <Headline title='' id='' subreddit='' thumbnail='' />}
      </div>
    )
}
