import React from 'react'
import WeatherTile from './WeatherTile'
import DayNightCycle from './DayNightCycle'
import './WeatherSection.css'
import moment from 'moment'

class WeatherSection extends React.Component {
  constructor(props) {
    super(props)
    this.RequestWeather = this.RequestWeather.bind(this)
    this.Rotate = this.Rotate.bind(this)
    this.sunset = this.sunset.bind(this)


    this.state = {
      isLoading: false,
      weatherData: null,
      location: null
    }
  }

  RequestWeather() {
    fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=Winnipeg&days=2`)
    //fetch(`http://api.weatherapi.com/v1/forecast.json?key=6018bc6ca5bb4a4884c184418202812&q=${this.state.location}&days=5`)
    .then(response => response.json())
    .then(json => {
      console.log(json)
      this.setState({
        isLoading: true,
        currentData: json.current,
        weatherData: json.forecast.forecastday,
        })
        //console.log(moment().format('LT'))
        console.log(this.state.weatherData[0].astro.sunset)
        this.DayOrNight()

      }
    ).catch(() => {
      console.log('error')
    })
  }
  componentDidMount() {
    this.RequestWeather()
    setInterval(this.RequestWeather, 60000)
  }
  Rotate() {
    let degrees = 0;
    let rotate = setInterval(() => {
      document.querySelector('.dayNightCycle').style.transform=`rotate({degrees}deg)`;
      degrees++;
    }, 10000)
  }
  sunset() {
    let sunsetStarter = moment(this.state.weatherData[0].astro.sunset, 'hh:mm A').subtract(15, 'minutes'),
    sunriseStarter = moment(this.state.weatherData[0].astro.sunrise, 'hh:mm A').subtract(15, 'minutes'),
    tillSunset = sunsetStarter.subtract(moment())
    if (moment('hh:mm A') == sunsetStarter) {

    }
    sunsetTimer = setTimeout(() => {
      document.querySelector('.dayNightCircle').style.animation = 'dayNightCycle 10s infinite 1s forward'
    }, tillSunset)

    console.log('TIME LEFT: ' + sunsetTimer)
  }
  render() {
    return(
    <div className="weatherBackground">
    <DayNightCycle />
    <div className="Tiles">
    <div className="nowWeather">
    {this.state.isLoading ?
    <WeatherTile icon={this.state.currentData.condition.icon} date="Now" today={this.state.currentData.temp_c} /> : null}
    </div>
    <div className="forecast">
  {this.state.isLoading ?
    this.state.weatherData.map(data => (<WeatherTile icon={data.day.condition.icon} date={data.date.split('-').slice(1,)} high={data.day.maxtemp_c} low={data.day.mintemp_c}/>)) : null }
    </div>
    </div>
    <div className="weatherHouse"></div>
    </div>
    )
  }
}
export default WeatherSection

