    this.setState({
      headlines: posts.slice(3,)
      })
      document.querySelector('.newsHeadline').removeEventListener('transitionend', () => {
        this.setState({
          headlines: posts.slice(3,)
          })
      })
  })
  document.querySelectorAll('.newsHeadline').forEach(headline => {
    headline.classList.add('opacity0')
  })
      document.querySelectorAll('.newsHeadline').forEach(headline => {
        headline.classList.remove('opacity0')
      })
  } else {
    this.GetNews()
  }
}*/
ShuffleNews() {
  let posts = [...this.state.headlines]
if (this.state.headlines.length >= 3) {
  this.setState({
    headlines: posts.slice(3,)
  }, () => {
    document.querySelectorAll('.newsHeadline').forEach(headline => {
      headline.style.animation = ('newsOpacity 1s reverse forwards')
    })
  })
  } else {
    this.GetNews()
  }
}

  /*componentDidUpdate(prevState) {
    if (this.state.headlines !== prevState.headlines) {
    //setTimeout(this.ShuffleNews, 6000 * 10)
    setTimeout(this.ShuffleNews, 600 * 10)
    }
  }*/
  componentDidUpdate() {

  }
  componentDidMount() {
    this.GetNews()
    setInterval(this.ShuffleNews, 10000)
  }

  render() {
    return(
      <div className="news">
      {this.state.headlines != null ? this.state.headlines.slice(0,3).map(headline => {
        return <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
  }
}
