      } else if (this.state.sunPosition == 'goldenHourEnd') {
        sun.style.animation = "sunriseEndToGoldenEnd 15s"
        sun.style.animationDirection = "normal"
        sun.style.animationFillMode = 'forwards'
        sky.classList.remove('opacity2')
        sky.classList.add('opacity3')
