import React from 'react'
import moment from 'moment'
import './Calendar.css'
import CalendarDay from './CalendarDay'

export default class Calendar extends React.Component {
  constructor(props) {
    super(props)
    this.GetEvents = this.GetEvents.bind(this)
    this.state = {
      events: {}
    }
  }


  GetEvents() {
    let thisWeek = {
      0: [],
      1: [],
      2: [],
      3: [],
      4: [],
      5: [],
      6: []
    }

    let holidaysWeek = {
      0: [],
      1: [],
      2: [],
      3: [],
      4: [],
      5: [],
      6: []
    }

    let today = new Date()
    let oneWeek = new Date()
    oneWeek.setDate(oneWeek.getDate() + 6)
    function sortEvents() {
      fetch(`https://www.googleapis.com/calendar/v3/calendars/thdnkns@gmail.com/events?singleEvents=True&orderBy=startTime&timeMin=${today.toISOString()}&timeMax=${oneWeek.toISOString()}&key=AIzaSyDZgdchyvtfLwtT2s1JH5dxgnwd-2nI2dw`)
        .then(res => res.json())
        .then(events => {
          let eventsList = [...events.items]
          console.log(new Date(Date.parse(eventsList[0].start.dateTime)).getDate())
          eventsList.forEach(event => {
            if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate()) {
              thisWeek[0].push(event);
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 1) {
              thisWeek[1].push(event)
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 2) {
              thisWeek[2].push(event)
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 3) {
              thisWeek[3].push(event)
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 4) {
              thisWeek[4].push(event)
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 5) {
              thisWeek[5].push(event)
            } else if (new Date(Date.parse(event.start.dateTime)).getDate() == today.getDate() + 6) {
              thisWeek[6].push(event)
            }
          })
          this.setState({
            events: thisWeek
          })
        })
    }

    function sortHolidays() {
      fetch('https://www.googleapis.com/calendar/v3/calendars/en.canadian%23holiday%40group.v.calendar.google.com/events?key=AIzaSyDZgdchyvtfLwtT2s1JH5dxgnwd-2nI2dw')
        .then(res => res.json())
        .then(holidays => {
          console.log(holidays)
          let holidaysList = [...holidays.items]
          holidaysList.forEach(holiday => {
            if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate()) {
              holidaysWeek[0].push(holiday);
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 1) {
              holidaysWeek[1].push(holiday)
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 2) {
              holidaysWeek[2].push(holiday)
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 3) {
              holidaysWeek[3].push(holiday)
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 4) {
              holidaysWeek[4].push(holiday)
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 5) {
              holidaysWeek[5].push(holiday)
            } else if (new Date(Date.parse(holiday.start.dateTime)).getDate() == today.getDate() + 6) {
              holidaysWeek[6].push(holiday)
            }
          })
          this.setState({
            holidays: holidaysWeek
          })
        })
    }
  }

  componentDidUpdate() {
  }

  componentDidMount() {
    this.GetEvents()
  }

  render() {
    return(
    <div className="calendarDiv">
      <CalendarDay holidays={this.state.holidaysWeek[0]} events={this.state.events[0]} date={moment().format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[1]} events={this.state.events[1]} date={moment().add(1, 'days').format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[2]} events={this.state.events[2]} date={moment().add(2, 'days').format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[3]} events={this.state.events[3]} date={moment().add(3, 'days').format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[4]} events={this.state.events[4]} date={moment().add(4, 'days').format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[5]} events={this.state.events[5]} date={moment().add(5, 'days').format('MMM D')}/>
      <CalendarDay holidays={this.state.holidaysWeek[6]} events={this.state.events[6]} date={moment().add(6, 'days').format('MMM D')}/>
    </div>
    )
  }
}
