import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.ShuffleNews = this.ShuffleNews.bind(this)

    this.state = {
      newsJson: null,
      headlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard/top.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children.slice(0,24)];
      this.setState({
        headlines: posts
    })
  })
}

ShuffleNews() {
  if (this.state.headlines.length >= 3) {
    let posts = this.state.headlines.slice(3)
    console.log('POSTS: ' + posts)
    this.setState({
      headlines: posts
    })
} else {
  this.GetNews()
  }
}

  componentDidUpdate() {
    setTimeout(this.ShuffleNews, 60000 * 10)
  }

  componentDidMount() {
    this.GetNews()
    //const getNews = setInterval(this.GetNews, 600000);
  }

  render() {
    console.log(this.state.headlines)
    return(
      <div className="news newsNight">
      {this.state.headlines != null ? this.state.headlines.slice(0,3).map(headline => {
        return <Headline title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
  }
}
