import React from 'react'
import './DayNightCycle.css'
import suncalc from 'suncalc'

export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: ''
    }
    //this.checkSunState = this.checkSunState.bind(this)
    this.SunPos = this.SunPos.bind(this)
  }

  SunPos() {
    const sun = document.querySelector('.Sun')
    const sky = document.querySelector('day')
    const sunYellow = document.querySelector('.sunYellow')
    const sunOrange = document.querySelector('.sunOrange')
    const sunRed = document.querySelector('.sunRed')

    const positions = ['noon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', '' ]
    const now = new Date()
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() == sunTimes[i].getHours()) {
        if (now.getMinutes() == sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] == sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                })
              }
            }
          }
        }
      }
    }
    if (this.state.sunPosition != null) {
      if (this.state.sunPosition == 'noon') {

      } else if (this.state.sunPosition == 'goldenHour') {
          sky.classList.add('opacity1')
          sun.classList.add('goldenHour')
          sunYellow.classList.add('opacity2')
          sunOrange.classList.add('opacity2')
      } else if (this.state.sunPosition == 'sunsetStart') {
        sun.classList
          .remove('goldenHour')
          .add('sunsetStart')
        sunRed.classList.add('opacity2')

      } else if (this.state.sunPosition == 'sunset') {
        sky
          .classList.remove('opacity1')
          .classList.add('opacity2')
        sun.classList.add('sunset')
        sunYellow
          .classList.remove('opacity2')
          .classList.add('opacity1')
        sunOrange
          .classList.remove('opacity2')
          .classList.add('opacity3')
        sunRed
          .classList.remove('opacity2')
          .classList.add('opacity3')
      } else if (this.state.sunPosition == 'dusk' || this.state.sunPosition == 'nauticalDusk') {
        sky
          .classList.remove('opacity2')
          .classList.add('opacity3')
        sun
          .classList.remove('sunset')
          .classList.add('dusk')
      }
    }
  }

  componentDidMount() {
    //this.checkSunState();
    const sunPosTimer = setInterval(this.SunPos, 30000)
  }

  render() {
    return(
      <div className="night">
      <div className="day">
      </div>
        <div className="Sun">
          <div className="sunYellow"></div>
          <div className="sunOrange"></div>
          <div className="sunRed"></div>
        </div>
        <div className="Moon"></div>
      </div>
    )
  }
}
