import React from 'react'
import picOne from './Pics/1.jpg'
import picTwo from './Pics/2.jpg'
import picThree from './Pics/3.jpg'
import picFour from './Pics/4.jpg'


import ('./Pics.css')

export default class Pics extends React.Component {
  constructor(props) {
    super(props)
    this.PicChooser = this.PicChooser.bind(this)
    this.state = {
      pics: null
    }
  }

  componentDidUpdate() {
    setTimeout(this.PicChooser, 600000)
  }

  componentDidMount() {
    this.PicChooser()
  }

  PicChooser() {
    const pics = [
      picOne,
      picTwo,
      picThree,
      picFour
      /*vidOne,
      vidTwo,
      vidThree*/
    ]
    const chosenOne = pics[Math.floor(Math.random() * pics.length)]
    this.setState({
      pic: chosenOne
    })
  }

  render() {
    return(
      <div className="Pics" >
      <div className="picContainer" style={{backgroundImage: `url('${this.state.pic}')`}}></div>
      </div>
    )
  }
}
