import React from 'react'
import Headline from './Headline'

import ('./News.css')

export default class News extends React.Component {
  constructor(props) {
    super(props)
    this.GetNews = this.GetNews.bind(this)
    this.ShuffleNews = this.ShuffleNews.bind(this)

    this.state = {
      newsJson: null,
      headlines: null,
      shownHeadlines: null
    }
  }
  GetNews() {
    fetch('https://old.reddit.com/user/d3RUPT/m/dashboard/top.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children.slice(0,23)];
      this.setState({
        headlines: posts
    })
  })
}

ShuffleNews() {
  document.querySelectorAll('.newsHeadline').forEach(headline => {
    headline.classList.add('opacity0')
  })
  let posts = [...this.state.headlines]
if (this.state.headlines.length >= 3) {
  setTimeout(() => {
    document.querySelectorAll('.newsHeadline').forEach(headline => {
      headline.classList.remove('opacity0')
    })
  }, 1100)
    this.setState({
      headlines: posts.slice(3,)
      })


  } else {
    this.GetNews()
  }
}

