import React from 'react'
import picOne from './1.jpg'
import picTwo from './2.jpg'
import picThree from './3.jpg'
import picFour from './4.jpg'
import vidOne from './1.mp4'
import vidTwo from './2.mp4'
import vidThree from './3.mp4'

import ('./Pics.css')

export default class Pics extends React.Component {
  constructor(props) {
    super(props)
    this.PicChooser = this.PicChooser.bind(this)
    this.state = {
      pics: null
    }
  }

  componentDidMount() {
    this.PicChooser()
  /*  this.setState({
      pic: pics[Math.floor(Math.random() * pics.length)]
    })

    const picChooser = setInterval(() => {
      this.setState({
        pic: pics[Math.floor(Math.random() * pics.length)]
      })
    }, 100000)*/
  }

  PicChooser() {
    const pics = [
      picOne,
      picTwo,
      picThree,
      picFour
      /*vidOne,
      vidTwo,
      vidThree*/
    ]
    const chosenOne = pics[Math.floor(Math.random() * pics.length)]
    this.setState({
      pic: chosenOne
    })
    return (<div className="picContainer" style={{backgroundImage: `url('${chosenOne}')`}}></div>)
  }

  render() {
    return(
      <div className="Pics" >
      {this.PicChooser}
      </div>
    )
  }
}
