          moon.classList.remove('opacity2')
          moon.classList.add('opacity4')
          this.setState({
            animPos: 8
          })
        }
      }  else if (this.state.sunPosition === 'between11') {
        if (this.state.animPos != 22) {
          setTimeout(() => {
            document.querySelector('.nightBackground').classList.add('opacity4')
