          sunSunriseEnd.classList.add('opacity2')
          //sky colors
          dawn.classList.add('opacity2')
          dawn.classList.remove('opacity4')
          sunrise.classList.add('opacity3')

          moon.classList.remove('opacity2')
          moon.classList.add('opacity0')
          this.setState({
