  InitSun() {
    //console.log('FIRED')
    //const now = moment(new Date(), 'ddd MMM DD YYYY HH:mm')
    let now = moment().set({'year': 2021, 'month': 3, 'date': 21, 'hour': 4, 'minute': 30})
    now = moment(now, 'ddd MMM DD YYYY HH:mm')
    console.log(now)
    let tomorrow = new Date()
    tomorrow = tomorrow.setDate(tomorrow.getDate()+1)

    const sunTimes = suncalc.getTimes(now, this.props.latitude, this.props.longitude)
    console.log('SUNTIMES')
    console.log(sunTimes)

    const isThisForToday = (pos) => {
        return moment(sunTimes[pos], 'ddd MMM DD YYYY HH:mm')
    }

    const times = [
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
      isThisForToday,
    ]

    const ranges = [
      moment.range(times[0]('nadir'), moment.range(times[0]('nadir'), times[1]('nightEnd')).center()), //nadir
      moment.range(times[1]('nightEnd'), moment.range(times[1]('nightEnd'), times[2]('nauticalDawn')).center()), //nightEnd
      moment.range(times[2]('nauticalDawn'), moment.range(times[2]('nauticalDawn'), times[3]('dawn')).center()), //nauticalDawn
      moment.range(times[3]('dawn'), moment.range(times[3]('dawn'), times[4]('sunrise')).center()), //dawn
      moment.range(times[4]('sunrise'), moment.range(times[4]('sunrise'), times[5]('sunriseEnd')).center()), //sunrise
      moment.range(times[5]('sunriseEnd'), moment.range(times[5]('sunriseEnd'), times[6]('goldenHourEnd')).center()), //sunriseEnd
      moment.range(times[6]('goldenHourEnd'), moment.range(times[6]('goldenHourEnd'), times[7]('solarNoon')).center()), //goldenHourEnd
      moment.range(times[7]('solarNoon'), moment.range(times[7]('solarNoon'), times[8]('goldenHour')).center()), //solarNoon
      moment.range(times[8]('goldenHour'), moment.range(times[8]('goldenHour'), times[9]('sunsetStart')).center()), //goldenHour
      moment.range(times[9]('sunsetStart'), moment.range(times[9]('sunsetStart'), times[10]('sunset')).center()), //sunsetStart
      moment.range(times[10]('sunset'), moment.range(times[10]('sunset'), times[11]('dusk')).center()), //sunset
      moment.range(times[11]('dusk'), moment.range(times[11]('dusk'), times[12]('nauticalDusk')).center()), //dusk
      moment.range(times[12]('nauticalDusk'), moment.range(times[12]('nauticalDusk'), times[13]('night')).center()), //nauticalDusk
      moment.range(times[13]('night'), times[0]('nadir')), //night
      moment.range(moment.range(times[1]('nadir'), times[2]('nightEnd')).center(), times[2]('nightEnd')), //betweenNadir&NightEnd 1
      moment.range(moment.range(times[2]('nightEnd'), times[3]('dawn')).center(), times[3]('dawn')), //betweenNightEnd&Dawn 2
      moment.range(moment.range(times[3]('dawn'), times[4]('sunrise')).center(), times[4]('sunrise')), //betweenDawn&Sunrise 3
      moment.range(moment.range(times[4]('sunrise'), times[5]('sunriseEnd')).center(), times[5]('sunriseEnd')), //betweenSunrise&SunriseEnd 4
      moment.range(moment.range(times[5]('sunriseEnd'), times[6]('goldenHourEnd')).center(), times[6]('goldenHourEnd')), //betweensunriseEnd&GodlenEnd 5
      moment.range(moment.range(times[6]('goldenHourEnd'), times[7]('solarNoon')).center(), times[7]('solarNoon')), //betweengoldenEnd&Noon 6
      moment.range(moment.range(times[7]('solarNoon'), times[8]('goldenHour')).center(), times[8]('goldenHour')), //betweenNoon&Golden 7
      moment.range(moment.range(times[8]('goldenHour'), times[9]('sunsetStart')).center(), times[9]('sunsetStart')), //betweenGolden&SunsetStart 8
      moment.range(moment.range(times[9]('sunsetStart'), times[10]('sunset')).center(), times[10]('sunset')), //betweenSunsetStart&Sunset 9
      moment.range(moment.range(times[10]('sunset'), times[11]('dusk')).center(), times[11]('dusk')), //betweenSunset&Dusk 10
      moment.range(moment.range(times[10]('night'), times[11]('nadir')).center(), times[11]('nadir')), //betweenSunset&Dusk 10
    ];
    //console.log(moment().isBefore(moment.range(times[0]('solarNoon'), times[0]('goldenHour')).center()))
   // console.log(moment.range(moment.range(times[7]('solarNoon'), times[8]('goldenHour')).center(), times[8]('goldenHour')))


        let skyDay = ['sunrise', 'between4', 'sunriseEnd', 'between5', 'goldenHourEnd', 'between6', 'solarNoon', 'between7', 'goldenHour', 'between8', 'sunsetStart', 'between9']
    for (let i = 0; i < ranges.length; i++) {
      //CHANGE TO SWITCH CASE
      if (now.within(ranges[i])) {
        //console.log('FIRED2')
        if (i == 0) {
          this.setState({
