import React from 'react'
import './DayNightCycle.css'
import suncalc from 'suncalc'
import Moment from 'moment'
import { extendMoment } from 'moment-range';
const moment = extendMoment(Moment);


export default class DayNightCycle extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      sunPosition: null,
      sunTimes: null,
      isDay: props.isDay
    }
    this.SunPos = this.SunPos.bind(this)
    this.setSunAnims = this.setSunAnims.bind(this)
    this.InitSun = this.InitSun.bind(this)
  }

  SunPos() {
    const now = new Date()
    console.log('TIME: ' + this.state.sunPosition)
    this.setState({
      sunTimes: suncalc.getTimes(now, 49.895077, -97.138451)
    })
    const sunPos = suncalc.getTimes(now, 49.895077, -97.138451)
    const sunTimes = Object.values(sunPos)
    for (let i = 0; i < sunTimes.length; i++) {
      if (now.getHours() === sunTimes[i].getHours()) {
        sunTimes[i].setMinutes(sunTimes[i].getMinutes(-15))
        if (now.getMinutes() === sunTimes[i].getMinutes()) {
          for (var ii in sunPos) {
            if (sunPos.hasOwnProperty(ii)) {
              if (sunPos[ii] === sunTimes[i])  {
                this.setState({
                  sunPosition: ii
                })
              }
            }
          }
        }
      }
    }

  }

  setSunAnims() {
    function getNextSunPos(pos) {
      const positions = ['solarNoon', 'goldenHour', 'sunsetStart', 'sunset', 'dusk', 'nauticalDusk', 'night', 'nadir', 'nightEnd', 'nauticalDawn', 'dawn', 'sunrise', 'sunriseEnd', 'goldenHourEnd']
      let nextPos = positions.indexOf(pos) +1;
      if (nextPos >= positions.length) {
        nextPos = 0;
        return(positions[nextPos])
      } else {
        return(positions[nextPos])
      }
    }

    const sun = document.querySelector('.Sun')
    const moon = document.querySelector('.Moon')
    const sky = document.querySelector('.night')
    const skyColor = document.querySelector('.sky')
    if (this.state.sunPosition !== null) {
      if ( this.state.sunPosition === 'sunrise') {
        if (this.state.animPos != 0) {
          sky.style.animation = "sunriseToEnd 5s forwards"
          sky.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sky.style.animationFillMode = 'forwards';

          sun.style.animation = "sunSunriseToEnd"
          sun.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
          sun.style.animationFillMode = 'forwards'

          skyColor.style.animation = "skySunriseToSunriseEnd"
          skyColor.style.animationDuration = `${(Math.abs(moment(this.state.sunTimes[getNextSunPos(this.state.sunPosition)], 'ddd MMM DD YYYY HH:mm:ss') - moment())) / 1000}s`;
