import React, {useState, useEffect} from 'react'
import Headline from './Headline'

import ('./News.css')

export default function News() {
  const [newsJson, getNewsJson] = useState(null);
  const [headlines, getHeadlines] = useState(null);
  const [shownHeadlines, getShownHeadlines] = useState(null)
  const [visible, setVisible] = useState(false)
  const [animConsts, setAnimConsts] = useState({indexes: [0, 1, 2], timeout: 500})
  useEffect(() => {
    GetNews()
  }, [])

  useEffect(() => {
    if (headlines) {
      animConsts.indexes.forEach(index => {
        setTimeout(() => {
          document?.getElementById(`headline${index}`)?.classList.remove('opacity0')
        }, animConsts.timeout*(index+1))
      })
    }
  }, [headlines]);


  const GetNews = () => {
    fetch('https://old.reddit.com/user/news-bot-dash/m/news/top.json')
    .then(res => res.json())
    .then(newsLinks => {
      let posts = [...newsLinks.data.children.slice(0,24)];
      setInterval(() => {
        if (posts.length >= 3) {
          animConsts.indexes.forEach(index => {
            setTimeout(() => {
              document?.getElementById(`headline${index}`)?.classList?.add('opacity0')
            }, animConsts.timeout*(index+1))
          })
          setTimeout(() => {
            getHeadlines(posts.slice(3,))
          }, 1550)
        } else {
          alert('NAH')
          GetNews()
        }
      }, 1000 * 30)
       getHeadlines(posts);
    })
  }

    return(
      <div className="news">
      {headlines ? headlines.slice(0,3).map((headline, index) => {
        return <Headline id={`headline${index}`} title={headline.data.title} subreddit={headline.data.subreddit_name_prefixed} thumbnail={headline.data.thumbnail} />
      }) : null}
      </div>
    )
}
