import React from 'react'
import Icon from '@mdi/react'
import { mdiArrowDownDropCircleOutline } from '@mdi/js';
import './WeatherTile.css';
export default WeatherTile;

class WeatherTile extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoading: false,
    }
  }
  componentDidMount(props) {
    let weather;
    if(props.today) {
      weather = <p>{props.today}</p>
    } else {
      weather = <p>{props.high} / {props.low}</p>
    }

    const tiles = document.querySelectorAll('.tile')
    if (props.isday == 0) {
      for (var tile in tiles) {
        tile.classList.add('tileNight');
      }
    } else if (props.isday == 1) {
      for (var tile in tiles) {
        tile.classList.remove('tileNight');
      }
    }
  }
  render() {
    return(
      <div className="tile">
        <img src={props.icon} alt='blak'/>
          <p className="tileDate">{props.date}</p>
          {weather}
          <p id="tileCondition">{props.condition}</p>
      </div>
    )
  }
}
