import React from 'react'
import './Clock.css'
import moment from 'moment'

export default class Clock extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      date: moment().startOf('day'),
      time: moment().format('LT')
    }
  }
  componentDidMount() {
    const now = new Date();
    const updateTime = setInterval(() => {
      this.setState({
        date: `${now.getDate()} `,
        time: ''
      })
    }, 4500)
  }
  render() {
    return(
      <div className="clock">
        <div className="timeDate"><p>{moment().format('llll')}</p></div>
        <div className="timeTime"><p>{moment().format('LT')}</p></div>
      </div>
    )
  }
}
