import React from 'react'

import ('./PicsNNews.css')

export default class PicsNNews extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      pic: ''
    }
  }
  componentDidMount() {
    const pics = [
      '1.jpg',
      '2.jpg',
      '3.jpg',
      '4.jpg',
      '1.mp4',
      '2.mp4',
      '3.mp4'
    ]
    const picChooser = setInterval(() => {
      this.setState({
        pic: pics[Math.floor(Math.random() * pics.length)]
      })
    }, 10000)
  }
  render() {
    return(
      <div className="picsNNews">
        <div className="Pics"></div>
        <div className="News">
        {this.state.pic.split('.')[1] == 'mp4'? <video src={`../../public/${this.state.pic}`} /> : <img src={this.state.pic} />}
        </div>
      </div>
    )
  }
}
