import React from 'react'
import './DayNightCycle.css'


export default function DayNightCycle() {
  function Rotate() {
    let degrees = 0;
    let rotate = setInterval(() => {
      document.querySelector('.dayNightCycle').style.transform=`rotate({degrees}deg)`;
      degrees++;
    })
  }
  return(
    <div className="dayNightCircle">
      <div className="Sun"></div>
      <div className="Moon"></div>
    </div>
  )
}
